import React, { useState, useEffect, useRef } from "react";
import "./SideMenu.css";

const SideMenu = ({ isVisible, closeMenu, handleMenuClick, setRecipeType }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false); // Control dropdown visibility
  const menuRef = useRef(null); // Create a ref for the side menu

  // Open the dropdown when "Recipes" is clicked, but only toggle on dish type selection
  const handleRecipesClick = () => {
    if (!dropdownVisible) {
      setDropdownVisible(true); // Show the dropdown if not visible
    }
  };

  // Handle dish type selection and close the dropdown after selecting
  const handleDishTypeSelect = (type) => {
    setRecipeType(type); // Set the selected dish type
    setDropdownVisible(false); // Close the dropdown after selecting
    handleMenuClick("Recipes"); // Trigger action for the selected menu
  };

  // Close dropdown when clicking outside of the menu
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setDropdownVisible(false); // Close dropdown if click is outside the menu
      }
    };
    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, []);

  return (
    <div className={`side-menu ${isVisible ? "visible" : ""}`} ref={menuRef}>
      <div className="side-menu-header">
        <h2>Welcome to the kitchen</h2>
      </div>

      <ul>
        <li onClick={handleRecipesClick}>
          Recipes
          {/* Show the dropdown if it's visible */}
          {dropdownVisible && (
            <ul className="dropdown-menu">
              <li onClick={() => handleDishTypeSelect("all")}>All Recipes</li>
              <li onClick={() => handleDishTypeSelect("meat")}>Meat</li>
              <li onClick={() => handleDishTypeSelect("fish")}>Fish</li>
              <li onClick={() => handleDishTypeSelect("vegan")}>Vegan</li>
            </ul>
          )}
        </li>
        <li onClick={() => handleMenuClick("Tips")}>Tips</li>
        <li onClick={() => handleMenuClick("Tools")}>Tools</li>
        <li onClick={() => handleMenuClick("Cleaning")}>Cleaning</li>
        <li onClick={() => handleMenuClick("Other Topics")}>Other Topics</li>
      </ul>
    </div>
  );
};

export default SideMenu;
