import React, { useState } from "react";
import "./SideMenu.css";

const SideMenu = ({ isVisible, closeMenu, handleMenuClick, selectedRecipeType, setRecipeType }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false); // Toggle for dropdown visibility

  // Toggle dropdown visibility
  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  // Handle dish type selection
  const handleDishTypeSelect = (type) => {
    setRecipeType(type); // Update the selected recipe type
    setDropdownVisible(false); // Close the dropdown after selection
  };

  return (
    <div className={`side-menu ${isVisible ? "visible" : ""}`}>
      {/* Side menu title */}
      <div className="side-menu-header">
        <h2>Welcome to the kitchen</h2>
      </div>

      {/* Menu options */}
      <ul>
        <li onClick={() => handleMenuClick("Recipes")}>
          Recipes
          {/* Dropdown for recipe types */}
          <div className="dropdown">
            <button onClick={toggleDropdown} className="dropdown-toggle">
              {selectedRecipeType ? selectedRecipeType.charAt(0).toUpperCase() + selectedRecipeType.slice(1) : "Select Type"}
            </button>
            {dropdownVisible && (
              <ul className="dropdown-menu">
                <li onClick={() => handleDishTypeSelect("all")}>All Recipes</li>
                <li onClick={() => handleDishTypeSelect("meat")}>Meat</li>
                <li onClick={() => handleDishTypeSelect("fish")}>Fish</li>
                <li onClick={() => handleDishTypeSelect("vegan")}>Vegan</li>
              </ul>
            )}
          </div>
        </li>
        <li onClick={() => handleMenuClick("Tips")}>Tips</li>
        <li onClick={() => handleMenuClick("Tools")}>Tools</li>
        <li onClick={() => handleMenuClick("Cleaning")}>Cleaning</li>
        <li onClick={() => handleMenuClick("Tips")}>Other Topics</li>
      </ul>

      {/* Close button */}
    </div>
  );
};

export default SideMenu;
