import React, { useState } from "react";
import "./SideMenu.css";

const SideMenu = ({ isVisible, closeMenu, handleMenuClick, selectedRecipeType, setRecipeType }) => {
  const [dropdownVisible, setDropdownVisible] = useState(false); // Dropdown visibility toggle

  // Open the dropdown when "Recipes" is clicked
  const handleRecipesClick = () => {
    setDropdownVisible(true); // Show the dish types dropdown
    handleMenuClick("Recipes"); // Stay in the "Recipes" menu
  };

  // Handle dish type selection
  const handleDishTypeSelect = (type) => {
    setRecipeType(type); // Update selected dish type
    setDropdownVisible(false); // Close dropdown after selecting a type
  };

  return (
    <div className={`side-menu ${isVisible ? "visible" : ""}`}>
      {/* Side menu title */}
      <div className="side-menu-header">
        <h2>Welcome to the kitchen</h2>
      </div>

      {/* Menu options */}
      <ul>
        <li onClick={handleRecipesClick}>
          Recipes
          {/* Dropdown for recipe types */}
          {dropdownVisible && (
            <ul className="dropdown-menu">
              <li onClick={() => handleDishTypeSelect("all")}>All Recipes</li>
              <li onClick={() => handleDishTypeSelect("meat")}>Meat</li>
              <li onClick={() => handleDishTypeSelect("fish")}>Fish</li>
              <li onClick={() => handleDishTypeSelect("vegan")}>Vegan</li>
            </ul>
          )}
        </li>
        <li onClick={() => handleMenuClick("Tips")}>Tips</li>
        <li onClick={() => handleMenuClick("Tools")}>Tools</li>
        <li onClick={() => handleMenuClick("Cleaning")}>Cleaning</li>
        <li onClick={() => handleMenuClick("Other Topics")}>Other Topics</li>
      </ul>

      {/* Close button */}
    </div>
  );
};

export default SideMenu;
