import React from "react";
import "./SideMenu.css";

const SideMenu = ({ isVisible, closeMenu, handleMenuClick, toggleMenu }) => {
  return (
    <div>
      {/* Button to open the side menu */}
      <button className="side-menu-toggle" onClick={toggleMenu}>
        â˜° {/* This is a hamburger icon, you can replace it with an image or custom icon */}
      </button>

      {/* Side menu */}
      <div className={`side-menu ${isVisible ? "visible" : ""}`}>
        {/* Side menu title */}
        <div className="side-menu-header">
          <h2>Welcome to the kitchen</h2>
        </div>

        {/* Menu options */}
        <ul>
          <li onClick={() => handleMenuClick("Recipes")}>Recipes</li>
          <li onClick={() => handleMenuClick("Tips")}>Tips</li>
          <li onClick={() => handleMenuClick("Tools")}>Tools</li>
          <li onClick={() => handleMenuClick("Cleaning")}>Cleaning</li>
          <li onClick={() => handleMenuClick("Tips")}>Other Topics</li>
        </ul>
      </div>
    </div>
  );
};

export default SideMenu;
