import React, { useState } from "react";
import SideMenu from "./SideMenu";
import "./Kitchen.css";

const Kitchen = () => {
  const [menuVisible, setMenuVisible] = useState(false);
  const [selectedMenu, setSelectedMenu] = useState(""); // Track selected menu item
  const [selectedRecipe, setSelectedRecipe] = useState(null); // Track the selected recipe

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  const closeMenu = () => {
    setMenuVisible(false);
  };

  // Simulated list of recipes with details
  const recipes = [
    { name: "Spaghetti Carbonara", details: "A classic Italian pasta dish made with eggs, cheese, pancetta, and pepper." },
    { name: "Grilled Chicken Salad", details: "A healthy salad with grilled chicken, mixed greens, tomatoes, cucumbers, and a vinaigrette dressing." },
    { name: "Chocolate Cake", details: "A rich and moist chocolate cake with a creamy chocolate frosting." },
    { name: "Vegetable Stir Fry", details: "A stir fry made with various vegetables like bell peppers, broccoli, and carrots, served with soy sauce." },
    { name: "Pancakes", details: "Fluffy pancakes served with syrup, butter, and optional fruit toppings." },
  ];

  // Handle menu item click
  const handleMenuClick = (menu) => {
    setSelectedMenu(menu); // Set the selected menu
    closeMenu(); // Close the side menu
    setSelectedRecipe(null); // Clear selected recipe when menu changes
  };

  // Handle recipe selection
  const handleRecipeClick = (recipe) => {
    setSelectedRecipe(recipe); // Set the selected recipe details
  };

  return (
    <div className="kitchen">
      <button
        className={`menu-toggle ${menuVisible ? "open" : ""}`}
        onClick={toggleMenu}
      >
        {menuVisible ? "×" : "☰"}
      </button>
      <SideMenu isVisible={menuVisible} closeMenu={closeMenu} handleMenuClick={handleMenuClick} />
      <div className="kitchen-content">
        <h1>Welcome to the Kitchen</h1>
        <p>Learn all about cooking, baking, and kitchen hacks!</p>

        {/* Conditionally render recipes list */}
        {selectedMenu === "Recipes" && (
          <div>
            <h2>Recipes</h2>
            <ul>
              {recipes.map((recipe, index) => (
                <li key={index} onClick={() => handleRecipeClick(recipe)}>
                  {recipe.name}
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Conditionally render recipe details */}
        {selectedRecipe && (
          <div className="recipe-details">
            <h2>{selectedRecipe.name}</h2>
            <p>{selectedRecipe.details}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Kitchen;
