import React, { useState } from "react";
import SideMenu from "./SideMenu";
import "./Kitchen.css";

const Kitchen = () => {
  const [menuVisible, setMenuVisible] = useState(false);
  const [selectedMenu, setSelectedMenu] = useState(""); // Track selected menu item
  const [expandedRecipe, setExpandedRecipe] = useState(null); // Track expanded recipe

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  const closeMenu = () => {
    setMenuVisible(false);
  };

  // Simulated list of recipes with details
  const recipes = [
    { id: 1, name: "Spaghetti Carbonara", details: "A classic Italian pasta dish made with eggs, cheese, pancetta, and pepper." },
    { id: 2, name: "Grilled Chicken Salad", details: "A healthy salad with grilled chicken, mixed greens, tomatoes, cucumbers, and a vinaigrette dressing." },
    { id: 3, name: "Chocolate Cake", details: "A rich and moist chocolate cake with a creamy chocolate frosting." },
    { id: 4, name: "Vegetable Stir Fry", details: "A stir fry made with various vegetables like bell peppers, broccoli, and carrots, served with soy sauce." },
    { id: 5, name: "Pancakes", details: "Fluffy pancakes served with syrup, butter, and optional fruit toppings." },
  ];

  // Handle menu item click
  const handleMenuClick = (menu) => {
    setSelectedMenu(menu); // Set the selected menu
    closeMenu(); // Close the side menu
    setExpandedRecipe(null); // Clear expanded recipe when menu changes
  };

  // Handle recipe click to toggle details
  const handleRecipeClick = (recipeId) => {
    setExpandedRecipe(expandedRecipe === recipeId ? null : recipeId); // Toggle recipe details visibility
  };

  return (
    <div className="kitchen">
      <button
        className={`menu-toggle ${menuVisible ? "open" : ""}`}
        onClick={toggleMenu}
      >
        {menuVisible ? "×" : "☰"}
      </button>
      <SideMenu isVisible={menuVisible}  handleMenuClick={handleMenuClick} />
      <div className="kitchen-content">

        {/* Conditionally render recipes list */}
        {selectedMenu === "Recipes" && (
          <div>
            <h2>Recipes</h2>
            <ul>
              {recipes.map((recipe) => (
                <li key={recipe.id}>
                  <span onClick={() => handleRecipeClick(recipe.id)} className="recipe-name">
                    {recipe.name}
                  </span>

                  {/* Conditionally render recipe details below the recipe name */}
                  {expandedRecipe === recipe.id && (
                    <div className="recipe-details">
                      <p>{recipe.details}</p>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Kitchen;
